    context "with different values" do
      let!(:project) { create(:metais_project) }
      let!(:isvs) { create(:metais_isvs, project: project) }
      let!(:isvs_version) { create(:metais_isvs_version, isvs: isvs) }

      it 'updates isvs and queues another jobs' do
        isvs.update(latest_version: isvs_version)

        expect(Metais::IsvsVersion.last).to have_attributes(
          isvs: isvs,
          nazov: "Centrálny ekonomický systém (CES MFSR)",
        )

        expect(Metais::Isvs.last).to have_attributes(
          latest_version: isvs_version,
